	smtpserver: "Zimbra",
	from: "monitoreo@aguakan.com",
	to: "rvalencia@aguakan.com; squintal@aguakan.com; gsacramento@aguakan.com; ccanto@aguakan.com; otrejo@aguakan.com",
	subject:alarmtext,
	body:"Favor de revisar niveles en: " + alarm.description.es + " " + value.toFixed(2) + "% o contactar a TI/Automatización para su revisión\n A las " + fdate
});
}

console.log("alarma enviada " + address);
