  switch(error.code) {
    case error.PERMISSION_DENIED:
      console.error("El usuario denegó el acceso a la geolocalización.");
      break;
    case error.POSITION_UNAVAILABLE:
      console.error("No se pudo determinar la ubicación.");
      break;
