script.code:
function updateRecipeStructure(configNode, callback) {
	if (typeof handleAccessControlResponse !== "undefined") {
		handleAccessControlResponse(
			{ node: "SYSTEM.LIBRARY.ATVISE.WEBMIMETHODS.GetRecipeData", right: "execute" },
			function () {
				//success function
				webMI.data.call("GetRecipeData", { mode: "Update", configNode: configNode }, function () {
					callback();
				});
			},
			function () {
				//error function
			}
		);
	}
}

/**
 * With the following object, we address linter warnings about unused variables and functions,
 * since these references will be shared with other linked display scripts.
 */
const updateStructureSharedReferences = {
	updateRecipeStructure
};
updateStructureSharedReferences;
