script.code:
var cp = new ChildProcess("node", {
    args: [
        "C:/SCADA-Node/sendToExpress.js",
        "Falla en banco de baterías",
        "BAT-003",
        "Crítica"
    ],
    workingDir: "C:/SCADA-Node"
});

if (cp.exec()) {
    console.log("✅ Script ejecutado desde Atvise.");
    console.log("STDOUT:", cp.stdOut);
    console.log("STDERR:", cp.stdErr);
} else {
    console.error("❌ Falló la ejecución del script desde Atvise.");
}
