			title:"T{Grafico Nivel}",toolbar:false,width:480,query:webMI.query});*/

			webMI.display.openDisplay("ObjectTypes.PROJECT.DataLogger"+ type +".Grafico_Sectorizacion",
			{base:  "AGENT.OBJECTS.Sofrel." + object}, "Sectorizacion");
			map.panTo(this.getPosition());
			map.setZoom(14);

			Markeract=true;
			Markeractdc=marker;
			Pos_origen=position;

			//Limpio
			Elimina_Ruta(Rutas);

		});
	/*	map.addListener("zoom_changed", () => {
    infowindow.setContent("Zoom: " + map.getZoom());
  });*/


		//CLICK (Abre Infowindow)
